import CheckoutContext from "./Checkout-context";
import {useReducer} from "react";

const defaultCheckoutState = {
    items: [],
    totalPrice: 0,
    totalCalories: 0
}

const checkoutReducer = (state, action) => {
    if(action.type === "ADD"){
        const updatedItems = state.items.concat();
        const updatedTotalPrice = state.totalPrice + action.item.price;
        const updatedTotalClories = state.totalCalories + action.item.calories;
        return {
            items: updatedItems,
            totalPrice: updatedTotalPrice,
            totalCalories: updatedTotalClories
        }
    }
    return defaultCheckoutState;    
}

const CheckoutProvider = (props)=> {
    
    const [checkoutState, dispachCheckoutAction] = useReducer(checkoutReducer, defaultCheckoutState);

    const addItemToCheckoutHandler = (item) => {
        dispachCheckoutAction({type: 'ADD', item: item})
    }

    const removeItemToCheckoutHandler = (id) => {
        dispachCheckoutAction({type: 'REMOVE', id: id})
    }

    const checkoutContext = {
        items: [] = checkoutState.items,
        totalAmount: checkoutState.totalAmount,
        totalCalories: 0,
        addItem: addItemToCheckoutHandler,
        removeItem: removeItemToCheckoutHandler
    };

    return <CheckoutContext.Provider value = {checkoutContext}> {props.children} </CheckoutContext.Provider>
}

export default CheckoutProvider;
