import {Fragment, useState} from 'react';
import Header from './components/Header/Header'
import NewOrder from './components/NewOrder/NewOrder';
import Modal from './components/UI/Modal';
import classes from './components/UI/Modal.module.css'
import Menu from './menu';

function App() {

  const [messageIsShown, setMessageIsShown] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);
  const [totalCalories, setTotalCalories] = useState(0);
  const [error, setError] = useState(null);

  const showMessageHandler = ()=> {
    setMessageIsShown(true);
  }
  const hideMessageHandler = ()=> {
    setMessageIsShown(false);
  }

  async function addOrderHandler(order) {
      console.log(JSON.stringify(order));
      setError(null);
      try{
        setMessageContent(order);
        showMessageHandler();
        const response = await fetch('http://localhost:3000/order', {
          mode: 'no-cors',
          method: 'POST',
          body: JSON.stringify(order),
          headers: {
            'Content-Type' : 'application/json'
          }
        });
        const data = await response.json();
        if(!response.ok){
          throw new Error('somethin went wrong!');
        }
        console.log(data);
      }
      catch(error){
          setError(error.message);
      }   
  }; 

  const setMessageContent = (order)=>{
    let mainDish = order["main dish"];
    let sideDish = order["side dish"];
    let drink = order["drink"];

    setTotalPrice(parseInt(Menu["main dish"][mainDish]["price"]) + parseInt(Menu["side dish"][sideDish]["price"]) 
    + parseInt(Menu["drink"][drink]["price"]));

    setTotalCalories(parseInt(Menu["main dish"][mainDish]["calories"]) + parseInt(Menu["side dish"][sideDish]["calories"]) 
    + parseInt(Menu["drink"][drink]["calories"]));
  };


  const ModalContent = (props) =>
  <Fragment> 
    <p> Successfully sent the order! </p>
    <div className = {classes.total}>
      <span> Total Price is : {totalPrice} </span>
    </div>
    <div className = {classes.total}>
      <span> Total Calories is : {totalCalories} </span> 
    </div>
    <div className= {classes.action}>
      <button className= {classes['button--alt']} onClick = {props.onClose}> Close </button>
    </div>
  </Fragment>

  return (
    <Fragment>
      <Header/>
      {!errror && messageIsShown && <Modal><ModalContent onClose = {hideMessageHandler}/></Modal>}
      {error && messageIsShown && <p> {error} </p> }
      <NewOrder onAddOrder = {addOrderHandler}/>
    </Fragment>
  );
}

export default App;
