import {Fragment, useState} from 'react';
import Header from './components/Header/Header'
import NewOrder from './components/NewOrder/NewOrder';
import Modal from './components/UI/Modal';
import classes from './Checkout.module.css'

function App() {

  const [messageIsShown, setMessageIsShown] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [didSubmit, setDidSubmit] = useState(false);


  // const[checkoutIsShown, setIsCheckoutShown] = useState(false);

  const showMessageHandler = ()=> {
    setMessageIsShown(true);
  }
  const hideMessageHandler = ()=> {
    setMessageIsShown(false);
  }

  const addOrderHandler = async (order)=> {
      console.log(order);
      setIsSubmitting(true)
      showMessageHandler();
      const response = await fetch('http://localhost:3000/order', {
        mode: 'no-cors',
        method: 'POST',
        body: JSON.stringify(order),
        headers: {
          'Content-Type' : 'application/json'
        }
      });
      const data = await response.json()
      console.log(data);
      setIsSubmitting(false);
      setDidSubmit(true);
  }; 



    const didSubmitModalContent = 
    <div> 
      <p> Successfully sent the order! </p>
      <div className = {classes.total}>
        <span> Total Price is : </span>
      </div>
      <div className = {classes.total}>
      <span> Total Calories is : </span> 
      </div>
      <div className= {classes.action}>
        <button className= {classes['button--alt']}> Close </button>
      </div>
    </div>

  return (
    <Fragment>
      <Header/>
      {didSubmit && <Modal>{didSubmitModalContent}</Modal>}
      <NewOrder onAddOrder = {addOrderHandler}/>
    </Fragment>
  );
}

export default App;
