import checkOutContext from "./checkout-context";
import {useReducer} from "react";

const defaultCheckoutState = {
    items: [],
    totalAmount: 0,
    totalCalories: 0
}

const checkoutReducer = (state, action) => {
    return defaultCheckoutState;    
}

const checkoutProvider = (props)=> {
    
    const [checkoutState, ] = useReducer(checkoutReducer, defaultCheckoutState);

    const addItemToCheckoutHandler = (item) => {}

    const removeItemToCheckoutHandler = (id) => {}

    const checkoutContext = {
        items: [],
        totalAmount: 0,
        totalCalories: 0,
        addItem: addItemToCheckoutHandler,
        removeItem: removeItemToCheckoutHandler
    };

    return <checkOutContext.Provider value = {checkoutContext}> {props.children} </checkOutContext.Provider>
}

export default checkoutProvider;
