import React, {useState} from 'react';
import './OrderForm.css';


const NewOrder = ()=> {

    const [enteredName, setEnteredName] = useState('');
    const [enteredPhone, setEnteredPhone] = useState('');
    const [enteredEmail, setEnteredEmail] = useState('');
    const [enteredAddress, setEnteredAddress] = useState('');
    const [enteredMainDish, setEnteredMainDish] = useState('');
    const [enteredSideDish, setEnteredSideDish] = useState('');
    const [enteredDrink, setEnteredDrink] = useState('');

    const nameChangeHandler = (event)=>{
        setEnteredName(event.target.value);
    };

    const phoneChangeHandler = (event)=>{
        setEnteredPhone(event.target.value);
    };

    const emailChangeHandler = (event)=>{
        setEnteredEmail(event.target.value);
    };

    const addressChangeHandler = (event)=>{
        setEnteredAddress(event.target.value);
    };

    const mainDishChangeHandler = (event)=>{
        setEnteredMainDish(event.target.value);
    };

    const sideDishChangeHandler = (event)=>{
        setEnteredSideDish(event.target.value);
    };

    const drinkChangeHandler = (event)=>{
        setEnteredDrink(event.target.value);
    };

    const submitHandler = (event)=> {
        event.preventDefault();

    }

    return <form onSubmit={submitHandler}>
        <div className="new-order__controls">
            <div className="new-order__control">
                <label> Name</label>
                <input type  = "text" placeholder="Enter your full name" onChange={nameChangeHandler}/>
            </div>
            <div className="new-order__control">
                <label> Phone Number</label>
                <input type = "number" placeholder="Enter your phone number" pattern ="[0-9]{3}-[0-9]{2}-[0-9]{3}" 
                onChange={phoneChangeHandler}/>
            </div>
            <div className="new-order__control">
                <label> Email</label>
                <input type = "email" placeholder="Enter your email address" pattern=".+@gmail.com" onChange={emailChangeHandler}/>
            </div>
            <div className="new-order__control">
                <label> Address</label>
                <input type = "text" placeholder="Enter your home address" onChange={addressChangeHandler}/>
            </div>
            <div className="new-order__control">
                <label> Main dish
                    <select onChange={mainDishChangeHandler}>
                        <option value="pizza">Pizza</option>
                        <option value="pasta">Pasta</option>
                        <option value="big_salad">Big salad</option>
                    </select>
                </label>
            </div>
            <div className="new-order__control">
                <label> Side dish
                    <select onChange={sideDishChangeHandler}>
                            <option value="rice">Rice</option>
                            <option value="green_beans">Green beans</option>
                            <option value="Small_salad">small salad</option>
                        </select>
                </label>
            </div>
            <div className="new-order__control">
                <label> Drink
                    <select onChange={drinkChangeHandler}>
                        <option value="water">Water</option>
                        <option value="soda">Soda</option>
                        <option value="coke">Coke</option>
                    </select>
                </label>
            </div>
        </div>
        <div className="new-order-actions">
            <button type= "submit"> Order </button>
        </div>
    </form>
};

export default NewOrder;
