import {Fragment, useState} from 'react';
import Header from './components/Header/Header'
import NewOrder from './components/NewOrder/NewOrder';
import Modal from './components/UI/Modal';
import classes from './components/UI/ModalMessageContent.module.css';
import Menu from './menu';

function App() {

  const [messageIsShown, setMessageIsShown] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);
  const [totalCalories, setTotalCalories] = useState(0);
  const [error, setError] = useState(false);

  const showMessageHandler = ()=> {
    setMessageIsShown(true);
  }
  const hideMessageHandler = ()=> {
    setMessageIsShown(false);
  }

  async function addOrderHandler(order) {
      console.log(JSON.stringify(order));
      setError(false);
      // check if all inputs are valid
      for (const value of Object.values(order)) {
        if(value === ""){
            setError(true);
        }
        console.log(value);
      }
      if(!error){
        try{
          setMessageContent(order);
          showMessageHandler();
          const response = await fetch('http://localhost:3000/order', {
            method: 'POST',
            body: JSON.stringify(order),
            headers: {
              'Content-Type' : 'application/json'
            }
          });
          const data = await response.json();
          if(!response.ok){
            throw new Error('something went wrong!');
          }
          console.log(data);
        }
        catch(error){
            setError(true);
        }  
      }
      else{
        showMessageHandler();
      }
  }; 

  const setMessageContent = (order)=>{
    let mainDish = order["main dish"];
    let sideDish = order["side dish"];
    let drink = order["drink"];

    setTotalPrice(parseInt(Menu["main dish"][mainDish]["price"]) + parseInt(Menu["side dish"][sideDish]["price"]) 
    + parseInt(Menu["drink"][drink]["price"]));

    setTotalCalories(parseInt(Menu["main dish"][mainDish]["calories"]) + parseInt(Menu["side dish"][sideDish]["calories"]) 
    + parseInt(Menu["drink"][drink]["calories"]));
  };



  const ModalConfirmContent = (props) =>
  <Fragment> 
    <div className = {classes.total}>
      <span> Total Price is : {totalPrice}â‚ª </span>
    </div>
    <div className = {classes.total}>
      <span> Total Calories is : {totalCalories} kcal  </span> 
    </div>
    <div className= {classes.actions}>
      <button className= {classes.button} onClick = {props.onClose}> Cancel </button>
      <button className= {classes.button} > Confirm </button>
    </div>
  </Fragment>

  const ModalSuccessContent = (props) =>
    <Fragment>
      <p className = {classes.para}> Your order is on the way! </p>
      <div className= {classes.actions}>
        <button className= {classes.button} onClick = {props.onClose}> Close </button>
      </div>
   </Fragment>

  const ModalErrorContent = (props) =>
    <Fragment>
        <p className = {classes.para}> There was a problem with your order, please try again later! </p>
        <div className= {classes.actions}>
          <button className= {classes.button} onClick = {props.onClose}> Close </button>
        </div>
    </Fragment>

  return (
    <Fragment>
      <Header/>
      {!error && messageIsShown && <Modal><ModalConfirmContent onClose = {hideMessageHandler}/></Modal>}
      {error && messageIsShown && <Modal> <ModalErrorContent onClose = {hideMessageHandler}/> </Modal>}
      <NewOrder onAddOrder = {addOrderHandler}/>
    </Fragment>
  );
}

export default App;
