import {Fragment, useState} from 'react';
import Header from './components/Header/Header'
import NewOrder from './components/NewOrder/NewOrder';
import Checkout from './components/Checkout/Checkout'
import CheckoutProvider from './store/CheckoutProvider'

function App() {

  const[checkoutIsShown, setIsCheckoutShown] = useState(false);

  const showCheckoutHandler = ()=> {
    setIsCheckoutShown(true);
  }
  const hideCheckoutHandler = ()=> {
    setIsCheckoutShown(false);
  }

  async function addOrderHandler(order){
      console.log(order);
      const response = await fetch('http://localhost:3000/order', {
        mode: 'no-cors',
        method: 'POST',
        body: JSON.stringify(order),
        headers: {
          'Content-Type' : 'application/json'
        }
      });
      const data = await response.json()
      console.log(data);
  }; 

  return (
    <CheckoutProvider>
      <Header/>
      {checkoutIsShown && <Checkout onClose={hideCheckoutHandler}/>}
      <NewOrder onShowCheckout = {showCheckoutHandler} onAddOrder = {addOrderHandler}/>
    </CheckoutProvider>
  );
}

export default App;
